mapper.select:
SELECT pp.project_type, d.dict_label as project_type_name, COUNT(*) as project_count, SUM(pp.project_budget) as total_budget FROM person_project pp INNER JOIN person_info p ON pp.person_id = p.id LEFT JOIN sys_dict_data d ON pp.project_type = d.dict_value AND d.dict_type = 'project_type' WHERE p.del_flag = '0' AND p.status = '0'
AND p.team_id = #{teamId}
GROUP BY pp.project_type, d.dict_label ORDER BY project_count DESC

SELECT pp.*, p.person_name FROM person_project pp INNER JOIN person_info p ON pp.person_id = p.id WHERE p.del_flag = '0' AND p.status = '0' AND pp.project_level IN ('national', 'provincial')
AND p.team_id = #{teamId}
ORDER BY pp.project_level, pp.start_date DESC LIMIT 20
